body {
  font-family: sans-serif;
  background-color: #f2f0ef;
  color: #6f5270;
  padding: 0;
  margin: 0;
  font-size: 16px;
}

header {
  min-height: 30vh;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
}

.logo {
  position: absolute;
  top: 5vh;
  width: 33vw;
  z-index: 1;
}

.header-background {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: 10;
}

.header-background img {
  height: 100%;
  position: initial;
}

.header-background-left,
.header-background-right {
  position: absolute;
  z-index: 2;
  width: 50%;
  height: 100%;
  background-color: transparent;
  background-position: center;
  background-size: cover;
}

.header-background-left {
  top: 0;
  left: 0;
}

.header-background-right {
  top: 0;
  right: 0;
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
}

.header-background-left img {
  height: 75%;
}

.header-background-right img {
  height: 60%;
}

.header-content {
  display: flex;
  flex-direction: column;
  align-items: center;
  z-index: 10;
}

.header-content h1 {
  text-align: center;
  font-weight: 600;
  max-width: 80vw;
  margin-top: 20vh;
  margin-bottom: 3rem;
  font-size: 9vw;
}

.header-content button {
  margin-top: 3rem;
  margin-bottom: 3rem;
  background-color: white;
  border: none;
  outline: none;
  color: inherit;
  padding: 1.5rem 3.5rem;
  border-radius: 12px;
  box-shadow: 0px 3px 6px 1px #b3b3b342, 1px 1px #a38e8e38;
  font-size: 3.75vw;
}

#Services {
  margin-top: 5vh;
}

.services-wrapper {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 5vw;
}

.services-wrapper h1 {
  font-size: 6.5vw;
  text-align: center;
}

.services-wrapper article {
  background-color: white;
  border-radius: 5vw;
  box-shadow: 0px 3px 6px 1px #b3b3b342, 1px 1px #a38e8e38;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 6vw 9vw 9vw;

  margin: 4rem 0;
}

.services-article-content {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

.services-article-content h2 {
  font-size: 5vw;
  text-align: center;
  margin: 0;
}

.services-article-content p {
  font-size: 4vw;
  text-align: center;
  line-height: 1.6;
}

.services-article-content button {
  background: none;
  outline: none;
  border: none;
  font-size: 4vw;
  color: inherit;
  border: 3px solid #6f5270;
  border-radius: 15px;
  width: 40%;
  padding: 1.25rem 0;
}

#Values {
  margin-top: 5vh;
  display: flex;
  flex-direction: column;
  align-items: center;
}

#Values h1 {
  font-size: 6.5vw;
  max-width: 90vw;
  text-align: center;
  margin-bottom: 10rem;
}

#Values article {
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-bottom: 15rem;
}
.values-article-heading {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.values-article-heading img {
  width: 11vw;
}

.values-article-heading h2 {
  font-size: 5vw;
}

.values-article-content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
}

.values-article-content p {
  font-size: 4vw;
  padding: 0;
  margin: 0;
  margin-bottom: 4rem;
  max-width: 80vw;
  line-height: 1.6;
}

.values-article-content button {
  background: none;
  outline: none;
  border: none;
  font-size: 4vw;
  color: inherit;
  border: 5px solid #6f5270;
  border-radius: 15px;
  width: 40%;
  padding: 1.25rem 0;
}

footer {
  background-color: white;
  width: 100%;
}

.footer-socials {
  display: flex;
  align-items: center;
  flex-direction: column;
  margin-bottom: 7.5rem;
}

footer h1 {
  font-size: 5vw;
}

ul {
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

li {
  padding: 0 4vw;
}

li img {
  width: 6vw;
}

.footer-copyright p {
  text-align: center;
  font-size: 2.5vw;
  margin: 0;
  padding-bottom: 3rem;
}

/* TABLET */
@media (min-width: 768px) {
  header {
    min-height: 70vh;
  }

  .logo {
    width: 25vw;
  }

  .header-content h1 {
    font-size: 8vw;
  }

  .header-content button {
    font-size: 2.5vw;
  }

  /* SERVICES */
  #Services h1 {
    font-size: 4vw;
    max-width: 70vw;
  }

  .services-articles-wrapper article {
    flex-direction: initial;
    justify-content: space-between;
    padding: 1rem 3rem;
  }
  .services-article-content {
    margin-left: 7vw;
    max-width: 50%;
    align-items: flex-start;
  }

  .services-article-content h2 {
    font-size: 2.75vw;
    align-self: flex-start;
  }
  .services-article-content p {
    max-width: 80%;
    font-size: 1.75vw;
    text-align: left;
  }
  .services-article-content button {
    width: 90%;
    font-size: 2vw;
  }

  .services-article-alt {
    flex-direction: row-reverse !important;
  }
}

